// Section 11 Lecture 61 - Post Item Component
import React, { Fragment } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Moment from "react-moment";
// used when we add a like, remove a like, delete a post,etc
import { connect } from "react-redux";

const PostItem = ({
  auth,
  post: { _id, text, name, avatar, user, likes, comments, date }
}) => {
  <div class="post bg-white p-1 my-1">
    <div>
      <a href="profile.html">
        <img
          class="round-img"
          src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200"
          alt=""
        />
        <h4>John Doe</h4>
      </a>
    </div>
    <div>
      <p class="my-1">
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint possimus
        corporis sunt necessitatibus! Minus nesciunt soluta suscipit nobis. Amet
        accusamus distinctio cupiditate blanditiis dolor? Illo perferendis
        eveniet cum cupiditate aliquam?
      </p>
      <p class="post-date">Posted on 04/16/2019</p>
      <button type="button" class="btn btn-light">
        <i class="fas fa-thumbs-up" />
        <span>4</span>
      </button>
      <button type="button" class="btn btn-light">
        <i class="fas fa-thumbs-down" />
      </button>
      <a href="post.html" class="btn btn-primary">
        Discussion <span class="comment-count">2</span>
      </a>
      <button type="button" class="btn btn-danger">
        <i class="fas fa-times" />
      </button>
    </div>
  </div>;
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

// uses auth's state so we can provide a button to delete a post/comment only if that post/comment belongs to that user
const mapStateToProps = state => ({
  auth: state.auth
});
export default connect(
  mapStateToProps,
  {}
)(PostItem);
