import {
  AlertActionTypes,
  ActionTypes,
  SetAlertAction,
  RemoveAlertAction
} from "../actions/types";
// const initialState = [];
// const initialState: any[] = [];
// const initialState = { msg: "string", alertType: "string", id: ["string"] };
const initialState: [{ msg: string; alertType: string; id: string }] = [
  { msg: "string", alertType: "string", id: "string" }
];

export default function(state = initialState, action: AlertActionTypes) {
  // const { type, payload } = action;

  switch (action.type) {
    case ActionTypes.SET_ALERT: {
      const typedAction = action as SetAlertAction;

      return { ...state, typedAction };
    }
    case ActionTypes.REMOVE_ALERT: {
      const typedAction = action as RemoveAlertAction;
      state.filter(alert => alert.id !== typedAction);
      // return state.id.filter(alert => alert.id !== typedAction);
      // return state.id.filter(alert => alert.id !== typedAction);
    }
    default:
      return state;
  }
}
