import {
  POST_ERROR,
  UPDATE_LIKES,
  DELETE_POST,
  ADD_COMMENT,
  REMOVE_COMMENT,
  PostActionTypes,
  ActionTypes,
  GetPostsAction,
  GetPostAction
} from "../actions/types";
import { Post } from "../types/Post";

const initialState = {
  posts: [],
  post: {
    user: null,
    text: "string",
    name: "string",
    avatar: "string",
    likes: [
      {
        user: null
      }
    ],
    comments: [
      {
        _id: "string",
        user: null,
        text: "string",
        name: "string",
        avatar: "string",
        date: Date
      }
    ],
    date: Date
  },
  loading: true,
  error: {}
};

export default function(state = initialState, action: PostActionTypes) {
  // const { type, payload } = action;

  switch (action.type) {
    case ActionTypes.GET_POSTS: {
      const typedAction = action as GetPostsAction;
      return {
        ...state,
        posts: typedAction.posts,
        loading: false
      };
    }

    case ActionTypes.GET_POST: {
      const typedAction = action as GetPostAction;
      return {
        ...state,
        post: typedAction.post,
        loading: false
      };
    }

    case ActionTypes.ADD_POST:
      return {
        ...state,
        post: [payload, ...state.posts],
        loading: false
      };

