import uuid from "uuid";
import { ActionTypes } from "./types";

export const setAlert = (
  msg: string,
  alertType: string,
  timeout = 5000
) => dispatch => {
  const id = uuid.v4();
  dispatch({
    type: ActionTypes.SET_ALERT,
    payload: { msg, alertType, id }
  });

  setTimeout(
    () => dispatch({ type: ActionTypes.REMOVE_ALERT, payload: id }),
    timeout
  );
};
